package do_While;

public class Do10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i, sq;
		i = 1;
		 do
		 {
			 System.out.print("\n value is "+i);
			 sq = i* i;
			 i = i+1;
			 System.out.print("\t square of is" +sq);
			 
			 
		 } while(i <=10);
		 

		 
		
		
	}

}
