package nested_loops;

public class Nested_loops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i , j;
		for (i = 5; i >=1; i--)
		{
			System.out.print("\n");
			
			for(j=1; j <=i; j++)
			{
				System.out.print("\t "+j);
			}
			
		}
	
		
		
		
		
	}

}
